import { IGraph } from '@antv/g6';
export type PlainObject = Record<string, any>;
export interface measureItem {
    name: string;
    value: number;
    formattedValue?: number;
    formattedUnit?: string;
}
export interface OriginNode {
    id: string;
    name: string;
    layerName: string;
    layer?: number;
    measure: measureItem;
    relatedMeasures?: measureItem[];
    compareMeasures?: measureItem[];
    x?: number;
    y?: number;
    style?: PlainObject;
}
export interface OriginEdge {
    id: string;
    source: string;
    target: string;
    measure: measureItem;
    name?: string;
    ratio?: number;
    style?: PlainObject;
}
export type Rankdir = 'TB' | 'LR';
export interface LayoutParams {
    rankdir: Rankdir;
    [key: string]: any;
}
export type LayerOrder = string[];
export type RatioMethod = 'proportion' | 'splitFlow' | 'both';
export interface OriginData {
    nodes: OriginNode[];
    edges: OriginEdge[];
}
export interface Props {
    data: OriginData;
    layerOrder?: LayerOrder;
    segmLayer?: string;
    ratioMethod?: RatioMethod;
    layout?: LayoutParams;
    className?: string;
    style?: PlainObject;
    onReady?: (graph: IGraph) => void;
}
